const fs = require('fs');
const path = require('path');

const INCOMING_DIR = '/oikos/mneme/.hegemonikon/incoming';

try {
  if (!fs.existsSync(INCOMING_DIR)) {
    return [];
  }

  const files = fs.readdirSync(INCOMING_DIR)
    .filter(f => f.endsWith('.md') && !f.startsWith('.'))
    .map(f => {
      const fullPath = path.join(INCOMING_DIR, f);
      const stat = fs.statSync(fullPath);
      const content = fs.readFileSync(fullPath, 'utf-8');
      return { filename: f, path: fullPath, size: stat.size, content: content, mtime: stat.mtime.toISOString() };
    })
    .filter(f => f.content.trim().length > 0);

  if (files.length === 0) {
    return [];
  }

  return files.map(f => ({ json: f }));
} catch (e) {
  throw new Error('Scan failed: ' + e.message);
}

const fs = require('fs');
const path = require('path');

const content = $input.item.json.content;
const filename = $input.item.json.filename;

const lines = content.split('\n');
let title = filename;
let score = '?';
let topics = [];
let abstract = '';
let source = '';
let url = '';
let targets = '';

for (const line of lines) {
  if (line.startsWith('# ')) {
    title = line.replace(/^#\s*\/eat\s*ÂÄôË£ú:\s*/, '').trim();
  }
  const scoreMatch = line.match(/Score[*]*:\s*([\d.]+)/);
  if (scoreMatch) score = scoreMatch[1];
  const topicMatch = line.match(/Topics[*]*:\s*(.+?)(?:\s*\||$)/);
  if (topicMatch) topics = topicMatch[1].split(',').map(t => t.trim());
  const sourceMatch = line.match(/Source[*]*:\s*(.+?)(?:\s*\||$)/);
  if (sourceMatch) source = sourceMatch[1].trim();
  const urlMatch = line.match(/URL[*]*:\s*(http\S+)/);
  if (urlMatch) url = urlMatch[1].trim();
  const targetMatch = line.match(/Ê∂àÂåñÂÖàÂÄôË£ú[*]*:\s*(.+)/);
  if (targetMatch) targets = targetMatch[1].trim();
}

const absIdx = lines.findIndex(l => l.startsWith('## Abstract'));
if (absIdx >= 0) {
  abstract = lines.slice(absIdx + 1, absIdx + 5).join(' ').trim().slice(0, 200);
}

const PROCESSED_DIR = '/oikos/mneme/.hegemonikon/processed';
if (!fs.existsSync(PROCESSED_DIR)) {
  fs.mkdirSync(PROCESSED_DIR, { recursive: true });
}

const now = new Date().toISOString().slice(0, 10);
const digestFilename = `digest_${now}_${filename}`;

const digest = `# Digest: ${title}\n\n> **Ê∂àÂåñÊó•**: ${now}\n> **Score**: ${score}\n> **Topics**: ${topics.join(', ')}\n> **Source**: ${source}\n> **URL**: ${url}\n> **Ê∂àÂåñÂÖà**: ${targets}\n\n## Abstract\n\n${abstract}\n\n---\n\n*Auto-processed by WF-03 (n8n)*\n`;

fs.writeFileSync(path.join(PROCESSED_DIR, digestFilename), digest, 'utf-8');
fs.renameSync($input.item.json.path, path.join(PROCESSED_DIR, `original_${filename}`));

return [{
  json: {
    title, score, topics, source, url, abstract, targets,
    status: 'digested', digestFile: digestFilename
  }
}];

const items = $input.all();
const now = new Date().toLocaleString('ja-JP', { timeZone: 'Asia/Tokyo' });

const summary = items.map(item => {
  const d = item.json;
  const emoji = parseFloat(d.score) >= 0.5 ? '‚≠ê' : 'üìÑ';
  return `${emoji} *${d.title}* (${d.score}) ‚Äî ${d.topics.join(', ')}`;
}).join('\n');

return [{ json: {
  text: `üì• Ê∂àÂåñÂÆå‰∫Ü (${now})\n\n${items.length}‰ª∂Âá¶ÁêÜ:\n${summary}`,
  count: items.length,
  items: items.map(i => i.json)
} }];

const { execSync } = require('child_process');

const data = $input.item.json;

let webhookUrl = '';
try {
  webhookUrl = execSync('echo $SLACK_WEBHOOK_URL', { encoding: 'utf-8' }).trim();
} catch(e) {}

if (!webhookUrl) {
  return [{ json: { slack: 'skipped', reason: 'SLACK_WEBHOOK_URL not set', message: data.text } }];
}

const payload = JSON.stringify({
  blocks: [
    { type: 'header', text: { type: 'plain_text', text: 'üì• Ê∂àÂåñÂÆå‰∫Ü: ' + data.count + '‰ª∂', emoji: true } },
    { type: 'section', text: { type: 'mrkdwn', text: data.text } },
    { type: 'context', elements: [
      { type: 'mrkdwn', text: '_WF-03 Incoming ‚Üí Digest | Hegemonik√≥n_' }
    ]}
  ]
});

try {
  const escaped = payload.replace(/'/g, "'\\''");
  execSync(`curl -s -X POST '${webhookUrl}' -H 'Content-Type: application/json' -d '${escaped}' --connect-timeout 5 --max-time 10`, { encoding: 'utf-8' });
  return [{ json: { slack: 'sent' } }];
} catch(e) {
  return [{ json: { slack: 'error', msg: (e.message || '').slice(0, 100) } }];
}
