const data = $input.item.json.body;
const items = data.items || [];

const errors = items.filter(i => i.status === 'error');
const warns = items.filter(i => i.status === 'warn');
const score = data.score || 0;

let severity = 'LOW';
if (errors.length >= 3 || score < 0.5) severity = 'CRITICAL';
else if (errors.length >= 1) severity = 'HIGH';
else if (warns.length >= 2) severity = 'MEDIUM';

return [{ json: {
  severity,
  score,
  errorCount: errors.length,
  warnCount: warns.length,
  errorNames: errors.map(e => e.name).join(', ') || '(none)',
  warnNames: warns.map(w => w.name).join(', ') || '(none)',
  slack: severity === 'CRITICAL' || severity === 'HIGH' ? 'would_send' : 'skipped',
  timestamp: new Date().toISOString()
} }];
