const { execSync } = require('child_process');

const data = $input.item.json.body || $input.item.json;
const items = data.items || [];
const score = data.score || 0;

const errors = items.filter(i => i.status === 'error');
const warns = items.filter(i => i.status === 'warn');

let severity = 'LOW';
if (errors.length >= 3 || score < 0.3) severity = 'CRITICAL';
else if (errors.length >= 1 || score < 0.5) severity = 'HIGH';
else if (warns.length >= 2 || score < 0.7) severity = 'MEDIUM';

const errorNames = errors.map(e => `${e.name}: ${(e.detail || '').slice(0,40)}`).join(', ');
const warnNames = warns.map(w => `${w.name}: ${(w.detail || '').slice(0,40)}`).join(', ');
const ts = data.timestamp || new Date().toISOString();

const result = {
  severity, score,
  errorCount: errors.length, warnCount: warns.length,
  errorNames: errorNames || '(none)',
  warnNames: warnNames || '(none)',
  timestamp: ts
};

if (severity === 'CRITICAL' || severity === 'HIGH') {
  let webhookUrl = '';
  try {
    webhookUrl = execSync('echo $SLACK_WEBHOOK_URL', { encoding: 'utf-8' }).trim();
  } catch(e) {}

  if (webhookUrl) {
    const icon = severity === 'CRITICAL' ? 'üö®' : 'üî¥';
    const scoreBar = '‚ñà'.repeat(Math.round(score * 10)) + '‚ñë'.repeat(10 - Math.round(score * 10));
    const payload = JSON.stringify({
      blocks: [
        { type: 'header', text: { type: 'plain_text', text: icon + ' HGK Health: ' + severity, emoji: true } },
        { type: 'section', fields: [
          { type: 'mrkdwn', text: '*Score:*\n' + (score * 100).toFixed(0) + '% [' + scoreBar + ']' },
          { type: 'mrkdwn', text: '*Errors (' + errors.length + '):*\n' + (errorNames || 'none') }
        ]},
        { type: 'context', elements: [
          { type: 'mrkdwn', text: 'Warns: ' + (warnNames || 'none') + ' | _WF-05 Auto-Alert_' }
        ]}
      ]
    });
    try {
      const escaped = payload.replace(/'/g, "'\''");
      execSync(`curl -s -X POST '${webhookUrl}' -H 'Content-Type: application/json' -d '${escaped}' --connect-timeout 5 --max-time 10`, { encoding: 'utf-8' });
      result.slack = 'sent';
    } catch(e) {
      result.slack = 'error: ' + (e.message || '').slice(0, 100);
    }
  } else {
    result.slack = 'skipped (SLACK_WEBHOOK_URL not set)';
  }
} else {
  result.slack = 'skipped (severity=' + severity + ')';
}

return [{ json: result }];
