const fs = require('fs');
const data = $input.item.json.body || {};
const now = new Date();

// staticData: ファイル I/O 不要の永続化
const state = $getWorkflowStaticData('global');

// 排他制御: 既存セッションを force-end
let previousSession = null;
if (state.session && state.session.status === 'active') {
  previousSession = { ...state.session };
  previousSession.status = 'force-ended';
  previousSession.endTime = now.toISOString();
  // 履歴は jsonl に追記 (長期蓄積)
  try {
    const historyFile = '/oikos/mneme/.hegemonikon/session_history.jsonl';
    fs.appendFileSync(historyFile, JSON.stringify(previousSession) + '\n', 'utf-8');
  } catch(e) {}
}

// 新セッション
state.session = {
  startTime: now.toISOString(),
  mode: data.mode || 'standard',
  context: data.context || '',
  agent: data.agent || 'unknown',
  status: 'active'
};

const result = { ...state.session, message: 'Session started' };
if (previousSession) {
  result.previousSession = 'force-ended at ' + previousSession.endTime;
}
return [{ json: result }];

const state = $getWorkflowStaticData('global');

if (!state.session || state.session.status !== 'active') {
  return [{ json: { action: 'none', reason: 'Session ' + (state.session ? state.session.status : 'missing'), shouldRemind: false } }];
}

const elapsedMs = Date.now() - new Date(state.session.startTime).getTime();
const elapsedHours = (elapsedMs / (1000 * 60 * 60)).toFixed(1);

// Slack URL 存在確認
const slackUrl = (process.env.SLACK_WEBHOOK_URL || '').trim();
const hasSlackUrl = slackUrl.length > 0 && slackUrl.startsWith('http');

return [{ json: {
  action: 'remind',
  shouldRemind: hasSlackUrl,
  elapsedHours,
  context: state.session.context || '(none)',
  mode: state.session.mode,
  slackPayload: hasSlackUrl ? JSON.stringify({
    blocks: [
      { type: 'header', text: { type: 'plain_text', text: '⏰ セッション継続中', emoji: true } },
      { type: 'section', text: { type: 'mrkdwn', text: 'セッション開始から *' + elapsedHours + '時間* 経過。\n\n> Context: ' + (state.session.context || '(none)') + '\n\n休憩や `/bye` のタイミングかもしれません。' } },
      { type: 'context', elements: [{ type: 'mrkdwn', text: '_WF-06 Session State Machine | Hegemonikón_' }] }
    ]
  }) : ''
} }];

const fs = require('fs');
const data = $input.item.json.body || {};
const state = $getWorkflowStaticData('global');

const endTime = new Date();
let session = state.session || { status: 'unknown' };
const startTime = new Date(session.startTime || endTime);
const durationMinutes = ((endTime - startTime) / (1000 * 60)).toFixed(0);
const durationHours = (durationMinutes / 60).toFixed(1);

session.status = 'ended';
session.endTime = endTime.toISOString();
session.durationMinutes = parseInt(durationMinutes);
session.subject = data.subject || 'Unknown';

// 履歴に追記
try {
  const historyFile = '/oikos/mneme/.hegemonikon/session_history.jsonl';
  fs.appendFileSync(historyFile, JSON.stringify(session) + '\n', 'utf-8');
} catch(e) {}

// staticData クリア
state.session = null;

return [{ json: {
  status: 'ended',
  duration: durationHours + 'h (' + durationMinutes + 'min)',
  subject: session.subject,
  mode: session.mode,
  startTime: session.startTime,
  endTime: session.endTime
} }];
